import paper from './paper.jpg';
import L1 from './LeonardoAI01.jpg';


import styled from 'styled-components'

/*
const Background = styled.div`
background: 
  repeating-linear-gradient(
    45deg, 
    #f0f0f0, 
    #f0f0f0 10px, 
    #dddddd 10px, 
    #dddddd 20px
  ),
  linear-gradient(
    to bottom, 
    #333333, 
    #555555
  );
background-size: 100% 20px, 100%;
background-attachment: fixed;
`;
*/

const Background = styled.div`
background-image: url("${paper}");
  background-color: rgba(255, 255, 255, 0.5); /* Cambia el último valor (0.5) para ajustar la transparencia */
  background-blend-mode: multiply; /* Puedes ajustar el modo de mezcla según tus preferencias */
  /* Resto de tus propiedades de estilo si las tienes */
`;


export default Background
